INSERT INTO dat."RangeAndCoverActions"
	("Id", "Name", "GroupId", "Modifier", "UseFoRKs", "UseWeaponRangeAdvantage", "UsePositionAdvantage", "UseStrideAdvantage", "IsOpenEnded", "Effect", "SpecialRestriction", "SpecialAction", "However")
VALUES
	(0, 'Close', 0, 0, false, true, true, true, false, 'Using Close, the character attempts to dash into position quickly. If successful, the character advances one range category for his weapon.', null, null, null),
	(1, 'Sneak In', 0, 0, true, true, true, true, false, 'Using camouflage and stealth, you move unseen toward your opponent''s position. If successful, the character advances one range category.', null, null, null),
	(2, 'Flank', 0, 0, true, true, true, true, false, 'Using your knowledge of battle, you outmaneuver your opponent. If successful, the character advances one range category.', null, null, null),
	(3, 'Charge', 0, 0, false, true, true, true, true, 'A player may choose to press on using his character''s Steel rather than Speed, Stealth or Tactics. He pricks up his nerve, dashes from cover and charges screaming at his opponent. This is a difficult and dangerous maneuver. If he wins the versus test, then he closes one range.', null, null, 'Your opponents may always shoot âeven if they lose the positioning test. If your Charge is successful, your opponent gets one free shot before you close in. If the Charge is tied, everyone on both teams gets to shoot. Very ugly. If the Charge is failed, your opponent gets two free shots plus whatever successes he spends on shooting.'),
	(4, 'Maintain', 1, 0, false, true, true, true, false, 'Using his footwork and reflexes, the character attempts to get the better of his opponent. If your opponent moves forward, you move back. If he moves back, you move forward. If the character is successful, he''s held his opponent at the current range.', null, null, null),
	(5, 'Hold', 1, 0, false, true, true, false, false, 'When using the Hold action, the team does not move. Feet planted, they hold their ground and let fly for all they''re worth.', null, 'There are three special effects for the Hold action. First, your opponent always moves in with a close-type maneuver or away with a withdraw-type. Second, you always shoot, even if you lose the maneuver test. If you win the maneuver test, you get a free shot in addition to your other actions. Finally, advantage dice from a position are carried over from Hold into your next maneuver.', null),
	(6, 'Withdraw', 2, 2, false, true, true, true, false, 'Using Speed to Withdraw you beat a hasty retreat. If successful, you withdraw one range category. Position dice are not carried to the next volley. You abandon your position. You may allocate margin of success to finding a new position in this maneuver if appropriate.', 'While this maneuver grants a +2D advant age, any actions taken while using Withdraw cost two successes. Taking a shot, shrugging off a wound or bolting a door closed requires that you win by at least two.', 'For two successes from a successful Withdraw action, you can remain at your current range but then consult your range chart to find your opponent''s adjusted range. You rerange your weapons!', null),
	(7, 'Sneak Out', 2, 0, true, true, true, true, false, 'Using guile and subterfuge, you move unseen away from your opponent''s position. If successful, the character withdraws one range category. Position dice are not carried to the next volley. You abandon your position. You may allocate margin of success to finding a new position in this maneuver if appropriate.', null, null, null),
	(8, 'Fall Back', 2, 0, true, true, true, true, false, 'Using sound tactics, you abandon your position to take up another, better rearward one. If successful, the character withdraws one range category. Position dice are not carried to the next volley. You abandon your position. You may allocate margin of success to finding a new position in this maneuver if appropriate.', null, 'For two successes from a successful Fall Back action, you can set your range to your current range but then consult your range chart to find your opponent''s range. You rerang e your weapons!', null),
	(9, 'Retreat', 2, 1, false, true, true, true, true, 'Sometimes you''ve had quite enough. Screw this position, it''s time to beat a retreat. This takes nerves! A player may choose to make tracks using his character''s Steel rather than Speed, Stealth or Tactics. He pricks up his nerve, dashes from cover and makes a hasty advance to the rear. If successful, the character withdraws one range category. Position dice are not carried to the next volley. You abandon your position. You may allocate margin of success to finding a new position in this maneuver if appropriate.', null, null, 'Your opponents may always, shoot âeven if they lose the positioning test. If the Retreat is successful, your opponent gets one free shot before you move out. If the Retreat is tied, your opponent gets two free shots. If the Retreat is failed, your opponents gets two free shots plus whatever he spends on his margin of success. Lastly, if you fail a Retreat, you hesitate in the next volley.');
